import React, { useState, useEffect } from 'react'

const RandomPokemonA = (props) => {

    // define where the data wil go when set
    const [pokemonData, setPokemonData] = useState({
        image: null,
        pokemonName: null,
        id: null,
        xp: null,
        height: null,
        weight: null,
        dataState: false
    })
          
    // populate the data on render with useEffect bu picking a random number as an ID and fetching the pokemon from the API
    useEffect(() => {
        const randomNumber = Math.floor(Math.random() * 150)
        const url = `https://pokeapi.co/api/v2/pokemon/${randomNumber}`
        fetch(url)
            .then(response => response.json())
            .then(data => {
                setPokemonData({
                    image: (data.sprites.other["official-artwork"].front_default),
                    pokemonName: (data.name),
                    id: (data.id),
                    xp: (data.base_experience),
                    height: (data.height),
                    weight: (data.weight),
                    dataState: (true)
                })
            })
    }, [])

    // export the pokemon data to local storage after converting it to string
    

    useEffect(() => {
      localStorage.setItem('pokemonA', JSON.stringify(pokemonData))

    }, [pokemonData])



    // the last data to be set synchronously is dataState, if this isn't ready yet show a 'Loading...'
    if (!pokemonData.dataState) {
        return <div className='"App-text'>Loading...</div>
    }

    // when all the data is set return the components 
    return (
        // console.log(pokemonData),
        <button type= "button" className='pokemonCard' style= {{ marginLeft: "3vw" }}>
            <img src={pokemonData.image} className="pokemonImage" alt='pokemon'></img>
            <div className='pokemonCardText'>
                <span >Name: {pokemonData.pokemonName}</span>
                <span >ID: {pokemonData.id}</span>
                <span >XP: {pokemonData.xp}</span>
                <span >Height: {pokemonData.height}</span>
                <span >Weight: {pokemonData.weight}</span>
            </div>
        </button>         
    ) 


}

export default RandomPokemonA
