import React, { useState } from 'react'
import '../App.css'
import logo from '../assets/PokeTrumps logo.png'
import RedButton from './subComponents/RedButton'


const MainBlock = () => {

// Components to show in MainBlock on start screen
      const startMain = () => {
        return(
            <div>
                <img 
                    src= {logo} 
                    className= "App-logo" 
                    alt= "logo" 
                    style= {{ width:525, height: 150 }} 
                />
                <p 
                className= "App-header-small">
                    are you ready to play?
                </p>
                <div className='App-footer'>
                <RedButton message= "START" onClick= "null"/>
                <RedButton message= "RULES" onClick= "null"/>
        </div>
            </div>
        )
    }

// Components to show in MainBlock on intro screen
    const introMain = () => {
        return (
            <div>
            <p 
            className= "App-header">
                Okay, let's battle some Pokemon!
            </p>
            </div>
        )
    }
    



    const [mainBlock, setMainBlock] = useState(startMain)
        
     
    
    return (mainBlock)
    }


export default MainBlock
