import React, { useState, use } from 'react'
import '../../App.css'


const RedButton = ({ message = "", actionResult= null}) => {

    const [action, setAction] = useState()
    
    const ClickButton = () => {
        return (
            useEffect(() => {
                setAction(actionResult)
                console.log("clicked")
            }, []) 
        )
    }

    
    return (
        <button 
            type="button" 
            className='Red-button'
            onClick={ClickButton(actionResult)} >
                {message}
        </button>
    )
}

export default RedButton
