import React, { useState } from 'react'
import '../../App.css'


const RedButton = ({ message = ""}) => {
    
    const clickButton = (action) => {
        console.log("clicked")
        action
    }

    return (
        <button 
            type="button" 
            className='Red-button'
            onClick={clickButton(action)} >
                {message}
        </button>
    )
}

export default RedButton
