import React, { useState, useEffect } from 'react'
import '../App.css'
import logo from '../assets/PokeTrumps logo.png'



const MainBlock = () => {

// useState to update the mainBlock******************************************************
    const [mainBlock, setMainBlock] = useState()


// START OF: 
// functions to hold different views of the mainBlock starting from last seen in the game
// -------------------------------------------------------------------------------------

    // Components to show in MainBlock on cards screen***********************************
    const cardsMain = () => {

        class Pokemon extends React.Component{
            constructor(){
              super()
                this.state = {
                    image: null,
                    name: null,
                    types: null,
                    abilities: [],
                    moves: [],
                    buttonState: false
                }
            }

            getPokemon(){
                const $randomNumber = Math.floor(Math.random() * 155)
                console.log("Generate Pokemon")
                const url = `https://pokeapi.co/api/v2/pokemon/${$randomNumber}`
                fetch(url)
                .then(response => response.json())
                .then(data => {
                    console.log(data)
                    this.setState({
                    image: data.sprites.other["official-artwork"].front_default,
                    name: data.name.toUpperCase(),
                    type: data.types[0].type.name.toUpperCase(),
                    abilities: [data.abilities[0].ability.name, data.abilities[1].ability.name].join(', ').toUpperCase(),
                    moves: [data.moves[0].move.name, data.moves[1].move.name, data.moves[3].move.name, data.moves[4].move.name].join(', ').toUpperCase(),
                    buttonState: true
                })
        })
              
            }
          
            render(){        
                return (
                    <><div className='Slate'>
                        <header className='Slate-header'>
                            <p className='App-text' style={{ marginRight: "22.5vw" }}>ROUND 1</p>
                            <img
                                src={logo}
                                className="App-logo-small"
                                alt="small logo" />
                        </header>

                        <p
                            className="App-text">

                        </p>
                    </div><div>
                            {this.state.buttonState &&
                                <div className='mainCard'>
                                    <img src={this.state.image} className="pokemonImage"></img>
                                    <div className='mainText'>
                                        <span className='pokemonName'>Name: {this.state.name}</span>
                                        <span className='pokemonType'>Type: {this.state.type}</span>
                                        <span className='pokemonAbilities'>Abilities: {this.state.abilities}</span>
                                        <span className='pokemonMoves'>Moves: {this.state.moves}</span>
                                    </div>
                                </div>}
                            <button
                                type="button" onClick={() => this.getPokemon()}
                                className='buttonGenerate'>
                                Generate
                            </button>
                        </div></>
            )}
        
    }


    // Components to show in MainBlock on intro screen***********************************
    const IntroMain = () => {

        useEffect(() => {
            const timer = setTimeout(() => {
              setMainBlock(cardsMain)
            }, 2000);
            return () => clearTimeout(timer);
        }, []);
        
        
        return (
            
            <div className='Slate'>
                <img 
                    src= {logo} 
                    className= "App-logo-small" 
                    alt= "small logo" 
                />
                <p 
                className= "App-text">
                    Okay, let's battle some Pokemon!
                </p>
            </div>
            

        )
    }


    // Components to show in MainBlock on rules screen***********************************
    const rulesMain = () => {
        return (
            <div className='Slate'>
                <img 
                    src= {logo} 
                    className= "App-logo-small" 
                    alt= "small logo" 
                />
                <p 
                className= "App-text">
                    In this game you will be battling a mixture of cute and fearsome
                    pokemon with an opponent in an attempt to win all 8 gym badges
                    and become the best poke-trainer in the land.
                    All you need to do is select the pokemon you want to play with.
                    Then, the pokemon with the highest number in a randomly selected 
                    stat (ID, Height, or Weight) will win!
                    Remember to take a close look at the pokemon your opponent is
                    choosing between to give yourself the best chance of victory!
                </p>
                <div>
                    <button 
                        type= "button" 
                        className='Red-button' 
                        onClick= {() => setMainBlock(cardsMain)}>
                            Okay, I'm ready to start
                    </button>
                </div>
            </div>
        )
    }


    // Components to show in MainBlock on start screen***********************************
    const startMain = () => {
        return (
            

            <div className='Slate'>
                <header className= "App-header">
                    Hello, welcome to 
                </header>
                <img 
                    src= {logo} 
                    className= "App-logo" 
                    alt= "large rocking logo" 
                    // style= {{ width:525, height: 150 }} 
                />
                <p 
                    className= "App-text">
                        are you ready to play?
                </p>
                <div 
                    className='App-footer'>
                        <button 
                            type= "button" 
                            className='Red-button' 
                            onClick= {() => setMainBlock(cardsMain)}>
                                START
                        </button>
                        <button 
                            type= "button" 
                            className='Red-button' 
                            onClick= {() => setMainBlock(rulesMain)}>
                                RULES
                        </button>
                    
                </div>
            </div>
        )
    }


// END OF: 
// functions to hold different views of the mainBlock------------------------------------
    
// Now everything is defined, load the startMain function to mainBlock to show it********
    
   
    useEffect(() => {
        setMainBlock(startMain)
    }, [])
    

    return (mainBlock)
}}


export default MainBlock
