import React, { useState } from 'react'

const RandomPokemon = () => {
        
    const [image, setImage] = useState(null)
    const [name, setName] = useState(null)
    const [type, setType] = useState(null)
    const [abilities, setAbilities] = useState([])
    const [moves, setMoves] = useState([])
    const [dataState, setDataState] = useState(false)

    const getPokemon = () => {
                
        const randomNumber = Math.floor(Math.random() * 155)
        const url = `https://pokeapi.co/api/v2/pokemon/${randomNumber}`
        fetch(url)
        .then(response => response.json())
        .then(data => {
            console.log(data)
            setImage(data.sprites.other["official-artwork"].front_default)
            setName(data.name.toUpperCase())
            setType(data.types[0].type.name.toUpperCase())
            setAbilities([data.abilities[0].ability.name, data.abilities[1].ability.name].join(', ').toUpperCase())
            setDataState(true)
        })

        return (
            <div>
                ...
            </div>
        ) 
    }

    return (getPokemon)
}

export default RandomPokemon
