import React, { useState, useEffect } from 'react'
import '../../App.css'


const RedButton = ({ message = "", action= null}) => {
    
    const ClickButton = (action = null) => {
        useEffect(() => {
            action
            console.log("clicked")
        }, [])
        
        
    }

    return (
        <button 
            type="button" 
            className='Red-button'
            onClick={ClickButton(action)} >
                {message}
        </button>
    )
}

export default RedButton
